		accountChanges 		: function(e){
			var data = e.data,
			index = this.lineDS.indexOf(data),
			beforeLine = this.lineDS.at(index-1);

			if(beforeLine){
				data.set("description", beforeLine.description);

				if(index==1){
					if(beforeLine.dr>0){
						data.set("dr", 0);
						data.set("cr", beforeLine.dr);
					}else{
						data.set("dr", beforeLine.cr);
						data.set("cr", 0);
					}

					this.changes();
				}
			}
