				dfd.reject(e.errorThrown);    				
		    });

		    return dfd;	    		    	
	    },	    	    
		save 				: function(){
	    	var self = this, obj = this.get("obj");
	    	obj.set("issued_date", kendo.toString(new Date(obj.issued_date), "s"));

	    	//Recurring
	    	if(this.get("saveRecurring")){
