			if(dataArr.length > 1) {
				for(var i = 0; i < dataArr.length - 1; i++) {
					var current = this.segmentItemDS.get(dataArr[i]);
					var last = this.segmentItemDS.get(dataArr[lastIndex]);
					if(current.segment_id === last.segment_id) {
						dataArr.splice(lastIndex, 1);
						break;
					}
				}
			}				
