		addRow 				: function(){
			var obj = this.get("obj");

			this.lineDS.add({
				transaction_id 		: obj.id,
				payment_method_id 	: 0,
				account_id 			: "",
				contact_id 			: "",
				description 		: "",
				reference_no 		: "",
