	        }
    	
        	//Dates
        	if(start && end){
        		start = new Date(start);
        		end = new Date(end);
        		displayDate = "From " + kendo.toString(start, "dd-MM-yyyy") + " To " + kendo.toString(end, "dd-MM-yyyy");
