			   	amount					: 0,
			   	discount 				: 0,
			   	tax 					: 0,
			   	rate					: 1,
			   	locale 					: banhji.locale,
			   	issued_date 			: new Date(),
			   	due_date 				: duedate,
			   	bill_to 				: "",
			   	ship_to 				: "",
			   	memo 					: "",
			   	memo2 					: "",
			   	status 					: 0,
			   	segments 				: [],
			   	//Recurring
			   	recurring_name 			: "",
			   	start_date 				: new Date(),
			   	frequency 				: "Daily",
			   	month_option 			: "Day",
			   	interval 				: 1,
			   	day 					: 1,
			   	week 					: 0,
			   	month 					: 0,
			   	is_recurring 			: 0
	    	});		    		
			
			var obj = this.dataSource.at(0);
