			this.depositDS.data([]);

			if(obj.deposit>0){				
				this.depositDS.add({				
					contact_id 			: obj.contact_id,								
					reference_id 		: id,				
					user_id 			: this.get("user_id"),				    		
				   	type				: "Customer_Deposit",
				   	amount				: obj.deposit*-1,			   	
				   	rate				: obj.rate,			   	
				   	locale 				: obj.locale,			   	
				   	issued_date 		: obj.issued_date			   	
		    	});
			}
