	    	if(this.get("saveRecurring")){
	    		this.set("saveRecurring", false);
	    		
	    		obj.set("number", "");
	    		obj.set("is_recurring", 1);
	    	}

	        //Mode
	    	if(obj.isNew()==false){
	    		//Line has changed
