			    	$.each(this.journalLineDS.data(), function(index, value){
						value.set("deleted", 1);
					});

					this.addJournal(obj.id);
		    	}
	    	}

	        //Reference
	    	if(obj.reference_id>0){
	    		var ref = this.referenceDS.get(obj.reference_id);
