					description 		: "",
					reference_no 		: "",
					segments 	 		: value.segments,
					dr 	 				: value.amount,
					cr 					: 0,
					rate				: obj.rate,
					locale				: obj.locale
				});
			});

			//Tax on Dr
			if(!jQuery.isEmptyObject(taxList)){
				$.each(taxList, function(index, value){
					self.journalLineDS.add({
						transaction_id 		: transaction_id,
						account_id 			: value.id,
						contact_id 			: obj.contact_id,
						description 		: "",
						reference_no 		: "",
						segments 	 		: [],
						dr 	 				: value.amount,
						cr 					: 0,
						rate				: obj.rate,
						locale				: obj.locale
					});
				});
			}			

			//Discount on Cr
			if(obj.discount>0){
				this.journalLineDS.add({
					transaction_id 		: transaction_id,
					account_id 			: contact.trade_discount_id,
					contact_id 			: obj.contact_id,
					description 		: "",
					reference_no 		: "",
					segments 	 		: [],
					dr 	 				: 0,
					cr 					: obj.discount,
					rate				: obj.rate,
					locale				: obj.locale
				});
			}

			//A/R and Deposit on Cr
			if(!jQuery.isEmptyObject(returnList)){
				$.each(returnList, function(index, value){
					self.journalLineDS.add({
						transaction_id 		: transaction_id,
						account_id 			: value.id,
						contact_id 			: obj.contact_id,
						description 		: "",
						reference_no 		: "",
						segments 	 		: [],
						dr 	 				: 0,
						cr 					: value.amount,
						rate				: obj.rate,
						locale				: obj.locale
					});
				});
			}

			//INVENTORY
			//Inventory on Dr
			if(!jQuery.isEmptyObject(inventoryList)){
				$.each(inventoryList, function(index, value){
					self.journalLineDS.add({
						transaction_id 		: transaction_id,
						account_id 			: value.id,
						contact_id 			: obj.contact_id,
						description 		: "",
						reference_no 		: "",
						segments 	 		: [],
						dr 	 				: value.amount,
						cr 					: 0,
						rate				: value.rate,
						locale				: value.locale
					});
				});
			}

			//Cogs on Cr
			if(!jQuery.isEmptyObject(cogsList)){
				$.each(cogsList, function(index, value){
					self.journalLineDS.add({
						transaction_id 		: transaction_id,
						account_id 			: value.id,
						contact_id 			: obj.contact_id,
						description 		: "",
						reference_no 		: "",
						segments 	 		: [],
						dr 	 				: 0,
						cr 					: value.amount,
						rate				: value.rate,
						locale				: value.locale
					});
				});
			}

			this.journalLineDS.sync();
		}
	});
