	    			value.set("segments", obj.segments);
	    		});
			}

			//Obj
			this.objSync()
			.then(function(data){
				var ids = [];
				//Save journals
				$.each(data, function(index, value){
					var contact = banhji.source.customerDS.get(value.contact_id);
					ids.push(value.reference_id);

					//Cash on Dr
					self.journalLineDS.add({					
						transaction_id 		: value.id,
						account_id 			: obj.account_id,				
						contact_id 			: value.contact_id,				
						description 		: "",
						reference_no 		: "",
						segments 	 		: [],								
						dr 	 				: value.amount,
						cr 					: 0,				
						rate				: value.rate,
						locale				: value.locale
					});

					if(value.discount>0){
						//Discount on Dr
						self.journalLineDS.add({					
							transaction_id 		: value.id,
							account_id 			: contact.settlement_discount_id,				
							contact_id 			: value.contact_id,				
							description 		: "",
							reference_no 		: "",
							segments 	 		: [],								
							dr 	 				: value.discount,
							cr 					: 0,				
							rate				: value.rate,
							locale				: value.locale
						});
					}

					//AR on Cr
					self.journalLineDS.add({					
						transaction_id 		: value.id,
						account_id 			: contact.account_id,				
						contact_id 			: value.contact_id,				
						description 		: "",
						reference_no 		: "",
						segments 	 		: [],								
						dr 	 				: 0,
						cr 					: kendo.parseFloat(value.amount) + kendo.parseFloat(value.discount),				
						rate				: value.rate,
						locale				: value.locale
					});	
				});

				self.journalLineDS.sync();
				self.updateTxnStatus(ids);

				return data;
			}, function(reason) { //Error
				$("#ntf1").data("kendoNotification").error(reason);
			}).then(function(result){
				$("#ntf1").data("kendoNotification").success(banhji.source.successMessage);

				if(self.get("saveClose")){
					//Save Close
					self.set("saveClose", false);
					self.cancel();
					window.history.back();
				}else if(self.get("savePrint")){
					//Save Print
					self.set("savePrint", false);
					self.cancel();
					if(result[0].transaction_template_id>0){
						banhji.router.navigate("/invoice_form/"+result[0].id);
					}
				}else{
					//Save New
					self.addEmpty();
				}
			});
