		updateTxnStatus 	: function(ids){
			var self = this;

			if(ids.length>0){
				this.txnDS.query({
					filter:{ field:"id", operator:"where_in", value:ids }
				}).then(function(){
					var view = self.txnDS.view();

					$.each(view, function(index, value){
						if(value.amount_paid == 0){
							value.set("status", 0);
						}else if(value.amount_paid >= value.amount){
							value.set("status", 1);
						}else{
							value.set("status", 2);
						}
					});

					self.txnDS.sync();
				});
			}
		}
