			this.txnDS.data([]);
			this.journalLineDS.data([]);

			this.set("isEdit", false);
			this.set("obj", null);
			this.set("total", 0);
			this.set("total_received", 0);

			this.set("obj", {
				transaction_template_id : 12,
				account_id 			: 7,
				payment_method_id	: 1,
			   	rate				: 1,
			   	sub_total 			: 0,
			   	discount 			: 0,
